// 3rd party library imports
import P5 from 'p5';
import * as Tone from 'tone';

// project imports
import { Visualizer } from '../Visualizers';


export const BarsSeriesVisualizer = new Visualizer(
  'abdullahsharafsfsu',
  (p5: P5, analyzer: Tone.Analyser) => {
    const width = window.innerWidth;
    const height = window.innerHeight / 2;
    const dim = Math.min(width, height);

    p5.background(0, 0, 0, 255);

    p5.strokeWeight(dim * 0.01);
    p5.stroke(255, 255, 255, 255);
    p5.noFill();

    const values = analyzer.getValue();
    p5.beginShape();
    for (let i = 0; i < values.length; i++) {
      const amplitude = values[i] as number;
      const mapTop = p5.map(amplitude, 0, 1, 10, 100);
      const mapBottom = p5.map(amplitude, -1, 0, 10, 100);

      p5.line(i*5, height/2.5-2, i*5, height/4 + mapTop );
      p5.line(i*5, height/4 + mapBottom, i*5, height/2.5);

    }
    p5.endShape();
  },
);

// RESOURCES USED: https://p5js.org/reference/#/p5.FFT, STACK OVERFLOW, YOUTUBE
